import React, { Component } from 'react'

class EventBind extends Component {

    constructor(props) {
      super(props)

      this.state = {
         message: 'Hello'
      }
    }

    clickHandler(){
        this.setState({message:'Goodbye'})
    }

  render() {
    return (
      <div> {this.state.message} <br/>
        <button onClick={clickHandler}> Click </button>
      </div>
    )
  }
}

export default EventBind
